    int p0;
    int[] p1;
    int p2;

    // ----- test 0 -----
    p0 = 3;
    p1 = new int[] {6, 9, 8, 15, 7, 12};
    p2 = 11;
    all_right = KawigiEdit_RunTest(0, p0, p1, true, p2) && all_right;
    // ------------------

    // ----- test 1 -----
    p0 = 80;
    p1 = new int[] {3, 3, 3, 3, 3, 3, 3};
